DROP TABLE IF EXISTS `t_resource`;

CREATE TABLE `t_resource` (
  `id` int(10) NOT NULL auto_increment COMMENT '资源ID',
  `resource_name` varchar(50) NOT NULL COMMENT '资源名称',
  `resource_author` varchar(10) default 'admin' COMMENT '资源发布作者',
  `resource_summary` varchar(200) default NULL COMMENT '资源简介',
  `resource_location` varchar(200) NOT NULL COMMENT '资源地址',
  `resource_time` timestamp NOT NULL default CURRENT_TIMESTAMP on update CURRENT_TIMESTAMP COMMENT '资源更新时间',
  `module_id` int(10) default NULL COMMENT '资源所属栏目',
  `resource_type` int(10) default NULL COMMENT '资源类型',
  `page_id` int(10) default NULL COMMENT '资源位置',
  PRIMARY KEY  (`id`)
